<style amp-custom>
    body{font-family:'Noto Sans', sans-serif;font-size:16px;color:#222;background-color:#fff}*{-webkit-box-sizing:border-box;-moz-box-sizing:border-box;box-sizing:border-box}h1{font-size:2em;font-weight:400}.container{padding-right:20px;padding-left:20px}@media (min-width: 1200px){.container{width:1200px;margin-left:auto;margin-right:auto}}.container::after{display:block;clear:both;content:""}.page__header{color:#fff;background-color:#3c556d}.page__header .logo{display:inline-block;vertical-align:top;padding-top:16px;padding-bottom:8px}.page__header .logo svg,.page__header .logo svg *{fill:#fff}.page__header nav{display:inline-block;vertical-align:top;float:right}.page__header nav ul{margin:0;padding:0;list-style:none}.page__header nav ul:last-of-type{margin-right:-15px}.page__header nav ul li{display:inline-block;vertical-align:top}.page__header nav ul li a{display:block;padding:17px 15px;font-size:-875em;line-height:1em;text-decoration:none;color:#fff}.page__header nav ul li a:hover{text-decoration:none;background-color:#2c3e50}article{padding:40px 20px;border-bottom:1px solid #ddd}article:last-of-type{border:0}article h1{margin:0}article h1 a{text-decoration:none;color:inherit;transition:.3s}article small{opacity:.6}article .sticky-info{display:inline-block;float:none;margin-bottom:1.6em;padding:8px 20px;font-size:.75em;font-weight:800;text-transform:uppercase;letter-spacing:.1em;color:#000;background-color:#ffff00}article:first-of-type.has-post-thumbnail{position:relative;overflow:hidden;color:#fff;background-color:#222}article:first-of-type.has-post-thumbnail amp-img{margin:-40px -20px}article:first-of-type.has-post-thumbnail .post-body{position:absolute;right:20px;bottom:40px;left:20px}article:first-of-type.has-post-thumbnail .more-link{position:absolute;top:0;left:0;width:100%;height:100%;font-size:0}

